package client.itbt.yt.cn.customview;import android.app.Activity;import android.os.Bundle;import android.util.Log;import android.view.View;public class MainActivity extends Activity {    private CircleProgressBar circlebar;    private String TAG="MainActivity";    @Override    protected void onCreate(Bundle savedInstanceState) {        super.onCreate(savedInstanceState);        setContentView(R.layout.activity_main);        circlebar = (CircleProgressBar) findViewById(R.id.view_bar);    }    public void showYuanHuan(View view){        Log.i(TAG,"showYuanHuan");        circlebar.setStyle(0);        circlebar.setRoundColor(getResources().getColor(R.color.borrow_bar_min));        circlebar.setRoundProgressColor(getResources().getColor(R.color.colorAccent));        circlebar.setRoundWidth(6);        circlebar.setMax(100);        circlebar.setProgress(60);    }    public void showYuanHu(View view){        Log.i(TAG,"showYuanHu");        circlebar.setStyle(1);        circlebar.setRoundColor(getResources().getColor(R.color.borrow_bar_min));        circlebar.setRoundProgressColor(getResources().getColor(R.color.borrow_bar_min));        circlebar.setRoundWidth(6);        circlebar.setMax(100);        circlebar.setProgress(60);    }}